Ext.define('CatApp.controller.ClaimNumberController', {
    extend: 'Ext.app.Controller',

    config: {
        control: {
            "#claimNumberDetail #btnCallCompany": {
                tap: 'onBtnCallCompanyTap'
            },
            "#claimNumberDetail #btnSaveCompany": {
                tap: 'onBtnSaveCompanyTap'
            }
        }
    },

    onBtnCallCompanyTap: function(button, e, options) {
    	var phone = $("#Phone").html();
    	phone = phone.replace("-","");
    	phone = phone.replace("(","");
    	phone = phone.replace(")","");
    	phone = phone.replace(" ","");
		window.location = "tel:" + phone;
    },
    onBtnSaveCompanyTap: function(button, e, options) {
           var companyName = $("#CompanyName").html();
           navigator.notification.confirm('Contact: ' + companyName + ' will be added', confirmAction, 'Confirm Add Contact', 'OK, Cancel');
           }

});

function confirmAction(button){
    
	if(button == 2)
	{
		//do nothing
	}
	else if (button == 1)
	{
	
		var companyName = $("#CompanyName").html();
		var website = $("#Website").html();
        var phone = $("#Phone").html();
        var email = $("#Email").html();

		//create a new contact object
		var newContact = navigator.contacts.create();

		var phoneNums = [];
        phoneNums[0] = new ContactField('work', phone, true);
        newContact.phoneNumbers = phoneNums;
		
		newContact.nickname = companyName;
        newContact.displayName = companyName;
        
        var name = new ContactName();
        name.givenName = companyName;
        newContact.Name = name;
        
		//save contact to device
		newContact.save(onSuccess, onError);
	}
	
}

function onSuccess() {
    alert("Contact Saved");
};

function onError(contactError) {
    alert("Error Saving Contact: " + contactError.code);
};