Ext.define('CatApp.controller.StormContoller', {
    extend: 'Ext.app.Controller',

    config: {
        refs: {
            mainNav: 'mainnav',
            detailCard: '#stormDetail'
        },

        control: {
            "#mainNav #listStorms": {
                itemtap: 'onListstormsItemTap'
            },
            "#mainNav #notices": {
                itemtap: 'onNoticesItemTap'
            }
        }
    },

    onListstormsItemTap: function(dataview, index, target, record, e, options) {
        var me = this, 
        details;


        if(record)
        {
            details = Ext.create('CatApp.view.StormDetail', { title: ''});

            //set record details
            details.child('#map').setData(record.data);

            //need to load the details for notices 
            console.log('getting storm notices...');
            var store = Ext.data.StoreManager.lookup('StormNoticeStore'),
            url = 'http://catnj.faia.net/ResourceWebService.svc/GetStormNotices/' + record.data['ID'];

            store.getProxy().setUrl(url);
            store.load(store);

            me.getMainNav().push(details);
        }
    },

    onNoticesItemTap: function(dataview, index, target, record, e, options) {
        var me = this, 
        details;


        if(record)
        {
            details = Ext.create('CatApp.view.NoticeDetail', { title: ''});

            //set record details
            details.setData(record.data);

            me.getMainNav().push(details);
        }
    }

});